uniform sampler2D v0;
const vec2 v1 = vec2(8192.0,2.0);

void main() {
    float y;

float x0 = texture2D(v0, gl_FragCoord.xy / v1).r;

float v = exp(-2.0*abs(x0));
y = (1.0 - v) / (1.0 + v) * sign(x0);


    gl_FragColor = vec4(y, 0, 0, 0);
}
