const vec2 v6 = vec2(0.04,0.01);

void main() {
    ivec2 variable_position_c = (convert_position_fast(gl_FragCoord.yx,v0, v1, v2, 0));

    int m = variable_position_c.x;
    int n = variable_position_c.y;

    float v = 0.0;

    for (int k = 0; k < 25; k++) {
        vec4 v_a = texture2D(v3, (vec2(k, m) + 0.5) * v4);
        vec4 v_b = texture2D(v5, (vec2(k, n) + 0.5) * v6);

        v += dot(v_a, v_b);
    }

    gl_FragColor.r = v;
}

