const ivec2 v0 = ivec2(8192,1);
const ivec4 v1 = ivec4(49152,16384,128,1);
const ivec4 v2 = ivec4(1,3,128,128);
uniform sampler2D v3;
const ivec4 v4 = ivec4(12288,4096,64,1);
const ivec2 v5 = ivec2(1,8192);
const ivec2 v6 = ivec2(8192,2);
const vec2 v7 = vec2(0.0001220703125,0.5);

void main() {
    ivec4 variable_position_y = ((convert_position_fast(gl_FragCoord.yx,v0, v1, v2, 0)).xzwy);
    int n = variable_position_y.x;
    int h2 = variable_position_y.y;
    int w2 = variable_position_y.z;
    int c = variable_position_y.w;

    float sum = 0.0;

    for (int kh = 0; kh < 2; kh++) {
        int h1 = h2 + 0 - kh;
        if (h1 < 0 || h1 >= 64 * 2) continue;
        if (mod(h1, 2) != 0) continue;
        h1 /= 2;
        for (int kw = 0; kw < 2; kw++) {
            int w1 = w2 + 0 - kw;
            if (w1 < 0 || w1 >= 64 * 2) continue;
            if (mod(w1, 2) != 0) continue;
            w1 /= 2;

            sum += (texture2D(v3,((vec2((convert_position_fast((vec4(n, h1, w1, c).xwyz),v4, v5, v6, 0))) + 0.5) * v7))).r;
        }
    }

    gl_FragColor.r = sum;
}
